for arg in "${sequences[@]}"; do
	ordered=$(../push_swap $arg | ../checker_linux $arg)
	if [ "$ordered" == "OK" ]; then
		echo -e "${GREEN}OK${RESET}"
	else
		echo -e "${RED}KO${RESET}"
	fi
	count=$(../push_swap $arg | wc -l)
	if [ "$count" -ge 700 ]; then
		echo "$arg"
		echo -e "${RED}#########################${count}${RESET} #########################"
	else
		echo -e "${GREEN}${count}${RESET}"
	fi
done